import React, { useState } from 'react';

import TaskTodayList from './components/TaskTodays/TaskTodayList/TaskTodayList';
import CourseInput from './components/TaskTodays/CourseInput/CourseInput';
import './App.css';

const App = () => {
  const [TaskTodays, setTaskTodays] = useState([

  ]);

  const addtaskItemHandler = enteredText => {
    setTaskTodays(prevtask => {
      const updatedtask = [...prevtask];
      updatedtask.unshift({ text: enteredText, id: Math.random().toString() });
      return updatedtask;
    });
  };

  const deleteItemHandler = taskItemId => {
    setTaskTodays(prevtask => {
      const updatedtask = prevtask.filter(taskItem => taskItem.id !== taskItemId);
      return updatedtask;
    });
  };

  let content = (
    <p style={{ textAlign: 'center' }}>No task found. Maybe add one?</p>
  );

  if (TaskTodays.length > 0) {
    content = (
      <TaskTodayList items={TaskTodays} onDeleteItem={deleteItemHandler} />
    );
  }

  return (
    <div>
      <section id="taskItem-form">
        <CourseInput onAddtaskItem={addtaskItemHandler} />
      </section>
      <section id="task">
        {content}
        {/* {TaskTodays.length > 0 && (
          <TaskTodayList
            items={TaskTodays}
            onDeleteItem={deleteItemHandler}
          />
        ) // <p style={{ textAlign: 'center' }}>No task found. Maybe add one?</p>
        } */}
      </section>
    </div>
  );
};

export default App;
